import React, { useState } from 'react';
import { login } from '../../services/authService';
import '../style/Login.css'

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [keepSignedIn, setkeepSignedIn] = useState(false);

  const handleLogin = async () => {
    try {
      const token = await login(username, password);
      // Handle success, maybe store the token and redirect
      console.log(`Received login token ${token}`)
    } catch (error) {
      // Handle error, show an error message
      console.log(`Error while login ${error}`)
    }
  };

  return (
    <div className='login-container'>
      <div className='login-wrapper'>
        <label className='welcome-label'>Welcome Back!</label>
      </div>
      <div className='login-wrapper right-wrapper'>
        <div className='login-form'>
        <label className='login-title'>Login to your Account</label>
        <div className='input-container'>
          <label className='username-label'>Username</label>
          <input
           className='login-input'
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className='input-container'>
          <label className='password-label'>Password</label>
          <input
          className='login-input'
          type="Password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        </div>
        <div className='keep-signed-in'>
          <input
          className= 'checked'
          type='checkbox'
          value={keepSignedIn}
          onChange={(e) => setkeepSignedIn(e.target.value)}>
          </input>
          <label className='checkbox-label'>Keep me Signed in</label>
        </div>
        <button className='login-button' onClick={handleLogin}>Proceed</button>
        <button className='button-underline'>Sign Up</button>
        <button className='button-underline'>Forgot Password</button>
        </div>
      </div>
    </div>
  );
};

export default Login;
