import React, { useState } from 'react';
import { login } from '../../services/authService';
import '../style/Login.css'
import userImage from '../../assets/user.png'
import logo from '../../assets/G1614.svg'

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [keepSignedIn, setkeepSignedIn] = useState(false);

  const handleLogin = async () => {
    try {
      const token = await login(username, password);
      // Handle success, maybe store the token and redirect
      console.log(`Received login token ${token}`)
    } catch (error) {
      // Handle error, show an error message
      console.log(`Error while login ${error}`)
    }
  };

  return (
    <div className=' h-full flex '>
      <div className=' min-h-full min-w-[50%] bg-[#1a5de0] flex flex-col justify-center items-center '>
        <img src={logo} className=' mb-9 h-[28px] w-[28px] '/>
        <label className='text-[#ffffff] text-base font-semibold '>Welcome Back!</label>
      </div>
      <div className=' min-h-full min-w-[50%] flex flex-col justify-center items-center '>
        <div className='flex flex-col w-[35%] text-[#5e5e5e] text-sm '>
        <label className=' mb-6 text-base font-semibold '>Login to your Account</label>
        <div className='input-container'>
          <label className=' flex flex-col font-medium text-sm font-[Inter] '>Username</label>
          <input
           className=' mt-2 border-none bg-[#f5f5f5] h-12 rounded-md pl-[35px] font-[Inter] text-base font-normal '
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          />
          <img src={userImage} alt='username' className=' relative top-[-38px] left-1 h-7 w-7 '/>
        </div>
        <div className='input-container'>
          <label className=' flex flex-col font-medium text-sm font-[Inter] mt-[-10px] '>Password</label>
          <input
          className=' mt-2 border-none bg-[#f5f5f5] h-12 rounded-md pl-[10px] font-[Inter] text-base font-normal '
          type="Password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        </div>
        <div className=' flex text-xs font-normal items-center mb-4 mt-2 '>
          <input
          className= ' accent-[#1a5de0] mr-2 '
          type='checkbox'
          value={keepSignedIn}
          onChange={(e) => setkeepSignedIn(e.target.value)}>
          </input>
          <label className='checkbox-label'>Keep me Signed in</label>
        </div>
        <button className='login-button' onClick={handleLogin}>Proceed</button>
        <button className='button-underline'>Sign Up</button>
        <button className='button-underline'>Forgot Password</button>
        </div>
      </div>
    </div>
  );
};

export default Login;
