import React, { useState } from "react";
import { login } from "../../services/authService";
import "../style/Login.css";
import userImage from "../../assets/user.svg";
import logo from "../../assets/G1614.svg";
import arrow from "../../assets/arrow.svg";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [keepSignedIn, setkeepSignedIn] = useState(false);

  const handleLogin = async () => {
    try {
      const token = await login(username, password);
      // Handle success, maybe store the token and redirect
      console.log(`Received login token ${token}`);
    } catch (error) {
      // Handle error, show an error message
      console.log(`Error while login ${error}`);
    }
  };

  return (
    <div className=" h-full flex ">
      <div className=" min-h-full min-w-[50%] bg-[#1a5de0] flex flex-col justify-center items-center ">
        <img src={logo} className=" mb-9 h-[28px] w-[28px] " />
        <label className="text-[#ffffff] text-base font-semibold ">
          Welcome Back!
        </label>
      </div>
      <div className=" min-h-full min-w-[50%] flex flex-col justify-center items-center ">
        <div className="flex flex-col w-[35%] text-[#5e5e5e] text-sm ">
          <label className=" mb-6 text-base font-semibold ">
            Login to your Account
          </label>
          <div className="input-container">
            <label className=" flex flex-col font-medium text-sm font-[Inter] ">
              Username
            </label>
            <input
              className=" mt-2 border-none bg-[#f5f5f5] h-12 rounded-md pl-[35px] font-[Inter] text-base font-normal "
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <img
              src={userImage}
              alt="username"
              className=" relative top-[-38px] left-1 h-7 w-7 "
            />
          </div>
          <div className="input-container">
            <label className=" flex flex-col font-medium text-sm font-[Inter] ">
              Password
            </label>
            <input
              className=" mt-2 border-none bg-[#f5f5f5] h-12 rounded-md pl-[10px] font-[Inter] text-base font-normal "
              type="Password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className=" flex text-xs font-normal items-center mb-4 mt-2 ">
            <input
              className=" accent-[#1a5de0] mr-2 "
              type="checkbox"
              value={keepSignedIn}
              onChange={(e) => setkeepSignedIn(e.target.value)}
            ></input>
            <label className=" font-[Inter] text-base font-normal ">
              Keep me Signed in
            </label>
          </div>
          <button
            className=" h-12 rounded-md bg-[#1a5de0] text-[#ffffff] font-[Inter] text-base font-medium mb-6 "
            onClick={handleLogin}
          >
            <div className=" flex justify-center items-center ">
              <label className=" font-[Inter] text-base font-medium ">
                Proceed
              </label>{" "}
              <img src={arrow} />
            </div>
          </button>
          <div className=" flex flex-col gap-6 ">
            <button className=" underline text-[#1a5de0] flex items-start justify-start ">
              <label className=" font-[Inter] text-base font-medium ">
                Sign Up
              </label>
            </button>
            <button className=" underline text-[#1a5de0] flex items-start justify-start text-left ">
              <label className=" font-[Inter] text-base font-medium ">
                Forgot Password
              </label>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
