import React, { useState } from "react";
import { register } from "../../services/authService";
import { StaticLogo } from "./StaticLogo";
const Register = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [firstName, setfirstName] = useState("");
  const [lastName, setlastName] = useState("");

  const handleRegister = async () => {
    try {
      await register(username, password);
      // Handle success, maybe redirect or show a success message
    } catch (error) {
      // Handle error, show an error message
    }
  };

  return (
    <div className=" h-full flex ">
      <div className=" w-[50%] ">
        <StaticLogo text="Register yourself with Us!" />
      </div>
      <div className=" min-h-full w-[50%] flex flex-col justify-center items-center ">
        <div className="flex flex-col text-[#474747] text-sm ">
          <label className=" mb-6 text-base font-semibold ">
            Create New Account
          </label>
          <div className=" flex flex-row gap-6 ">
            <div className=" flex flex-col gap-2">
              <label className=" font-[Inter] text-sm font-medium ">
                First Name
              </label>
              <input
                className="  mt-2 border-none bg-[#f5f5f5] h-12 rounded-md pl-[35px] font-[Inter] text-base font-normal "
                type="text"
                placeholder="First Name"
                value={firstName}
                onChange={(e) => setfirstName(e.target.value)}
              ></input>
            </div>
            <div className=" flex flex-col gap-2">
              <label className=" font-[Inter] text-sm font-medium ">
                First Name
              </label>
              <input
                className="  mt-2 border-none bg-[#f5f5f5] h-12 rounded-md pl-[35px] font-[Inter] text-base font-normal "
                type="text"
                placeholder="Last Name"
                value={lastName}
                onChange={(e) => setlastName(e.target.value)}
              ></input>
            </div>
          </div>
          <div className=" flex flex-col gap-2">
            <label className=" font-[Inter] text-sm font-medium ">
              Username
            </label>
            <input
              className="  mt-2 border-none bg-[#f5f5f5] h-12 rounded-md pl-[35px] font-[Inter] text-base font-normal "
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            ></input>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
